<div id="icon-edit" class="icon32 icon32-posts-post"><br/></div><h2>	<?php _e('Edit Metabox', 'ecpt'); ?> - 	<a href="admin.php?page=easy-content-types/easy-content-types.php?metaboxes" title="Go Back"><?php _e('Go Back', 'ecpt'); ?></a></h2><?php if(isset($_GET['metabox-updated'])) : ?>	<div class="updated fade">		<p><?php _e('Meta Box updated.', 'ecpt'); ?></p>	</div><?php endif; ?>	<form method="POST" action="" id="metabox-field" >	<table class="form-table">		<tbody>		<?php		$i = 0;		foreach( $wpdb->get_results("SELECT * FROM " . $ecpt_db_meta_name . " WHERE id = '" . $_GET['metabox-edit'] . "';") as $key => $metabox)		{			$name = $metabox->name;			?>							<tr class="form-field">					<th scope="row" valign="top">						<label for="metabox-name"><?php _e('Name', 'ecpt'); ?></label>					</th>					<td>						<input type="text" id="metabox-name" name="metabox-name" value="<?php echo $metabox->nicename; ?>"  class="ecpt-text no-float"/>						<input type="hidden" id="metabox-old-name" name="metabox-old-name" value="<?php echo $metabox->name; ?>"/>						<p class="description"><?php _e('The meta box name is displayed at the top of the meta box on the editor screen', 'ecpt'); ?></p>					</td>				</tr>				<tr class="form-field">					<th scope="row" valign="top">						<label for="metabox-page"><?php _e('page', 'ecpt'); ?></label>					</th>					<td>						<select name="metabox-page" id="metabox-page" class="ecpt-text no-float"/>							<?php								$pages = get_post_types('', 'objects');								$metabox_pages = array();								foreach($pages as $page) { $metabox_pages[] = $page->name; }								$metabox_pages[] = 'link';																foreach ($metabox_pages as $metabox_page) {									echo '<option', $metabox->page == $metabox_page ? ' selected="selected"' : '', '>', $metabox_page, '</option>';								}											?>						</select>						<p class="description"><?php _e('The page is the post type that the meta box is displayed on. For example, if you choose "post", the meta box will be displayed on the regular post screen.', 'ecpt'); ?></p>					</td>				</tr>				<tr class="form-field">					<th scope="row" valign="top">						<label for="metabox-context"><?php _e('Context', 'ecpt'); ?></label>					</th>					<td>						<select name="metabox-context" id="metabox-context" class="ecpt-text no-float"/>							<?php								foreach ($metabox_contexts as $metabox_context) {									echo '<option', $metabox->context == $metabox_context ? ' selected="selected"' : '', '>', $metabox_context, '</option>';								}							?>						</select>						<p class="description"><?php _e('The context is the location on the editor screen thatthe metabox will be displayed', 'ecpt'); ?></p>					</td>				</tr>				<tr class="form-field">					<th scope="row" valign="top">						<label for="metabox-priority"><?php _e('Priority', 'ecpt'); ?></label>					</th>					<td>						<select name="metabox-priority" id="metabox-priority" class="ecpt-text no-float"/>							<?php								foreach ($metabox_priorities as $priority) {									echo '<option', $metabox->priority == $priority ? ' selected="selected"' : '', '>', $priority, '</option>';								}							?>						</select>						<p class="description"><?php _e('Priority determines how "high" on the editor screen the meta box appears', 'ecpt'); ?></p>					</td>				</tr>			<?php 		} ?>		</tbody>	</table>	<p class="submit">		<input type="hidden" name="ecpt-action" value="update-metabox"/>		<input type="hidden" name="metabox-id" value="<?php echo $metabox->id; ?>"/>		<input type="submit" id="<?php echo $_GET['metabox-edit'];?>" class="button-primary metabox-update" value="<?php _e('Update', 'ecpt'); ?>"/>	</p></form>